#!/bin/bash
echo -n "Hostname:" $(uname -n)", OS:" $(egrep -w "NAME|VERSION" /etc/os-release|awk -F= '{ print $2 }'|sed 's/"//g')", Kernel:" $(uname -ri)", LastBootUpTime:" $(uptime -s)", CPULoadPercent:" $(which top >/dev/null 2>&1 && (top -b -n 2 | grep 'Cpu(s)' | tail -n 1 | awk '{print $2}'| awk -F. '{print $1}')||echo top command not installed) ", MemoryLoadPercent:" $(free | grep Mem | awk '{print $3/$2 * 100.0}') ", SWAPLoadPercent:" $(free | grep 'Swap' | awk '{t = $2; f = $4; print (f/t)}') ", Filesystems:" $(df -TPh -x squashfs -x tmpfs -x devtmpfs | awk 'BEGIN {ORS=","} NR>1{print "[Mount:"$7"; UsedPercent:"$6"]"}')